import React, {Component} from 'react';
import Pokelist from './Pokelist'


class Pokecard extends Component {

    render(props){

        // const randomPoke = Pokelist[1];

            return (
            <div>
                <h2>{this.props.character.name}</h2>
                <img src = {`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${props.character.id}.png`}></img>
                <p>Type: {props.character.type}</p>
                <p>EXP: {props.character.base_experience}</p>
            </div>
        );
    }
}

export default Pokecard;
