import Pokecard from './Pokecard';
import Pokelist from './Pokelist';

class Pokedex extends Component {

    render() {

        let remainingPokedex = Pokelist;

        function randomPokeSelector(list){

            const randomPoke = list[Math.floor(Math.random() * list.length)];

            list = list.filter( item => item !== randomPoke ); 

            return randomPoke;
        }


        return (
            <div>
            <Pokecard character={randomPokeSelector(remainingPokedex)} />
