import React, {Component} from 'react';
import Pokelist from './Pokelist';
import './Pokecard.css';

class Pokecard extends Component {

    render(props){

        let remainingPokedex = Pokelist;

        function selectRandomPoke(list) {
            itemSelected = list[Math.floor(Math.random() * list.length)];
            lsit = list.filter(poke => poke !== itemSelected);

            return itemSelected;

        }

        let testPoke = selectRandomPoke(Pokelist);

        let randomPoke = remainingPokedex[Math.floor(Math.random() * remainingPokedex.length)];
        remainingPokedex = remainingPokedex.filter(poke => poke !== randomPoke);

        function idNumber(num) {

            if (num <= 9) {
                return num = '00' + num;
            } else if (num <= 99) {
                return num = '0' + num;
            } else {
                return num;
            }
        }

            return (
                <div className='Pokecard'>
                    <h2 className='Pokecard-h2'>{testPoke.name}</h2>
                    <h2 className='Pokecard-h2'>{randomPoke.name}</h2>
                    <img className='Pokecard-img' src={`https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${idNumber(randomPoke.id)}.png`}/>
                    <p className='Pokecard-p'>Type: {randomPoke.type}</p>
                    <p className='Pokecard-p'>EXP: {randomPoke.base_experience}</p>
                </div>

            // <div className= "Pokecard">
            //     <h2>{this.props.character.name}</h2>
            //     <img src = {`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${this.props.character.id}.png`}></img>
            //     <p>Type: {this.props.character.type}</p>
            //     <p>EXP: {this.props.character.base_experience}</p>
            // </div>
        );
    }
}

export default Pokecard;
