import React, {Component} from 'react';
import Pokelist from './Pokelist'


class Pokecard extends Component {

    render(props){

        const randomPoke = Pokelist[1];

            console.log(props)
            return (
            <div>
                <h2>{this.props.name}</h2>
                <img src = {`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${randomPoke.id}.png`}></img>
                <p>Type: {randomPoke.type}</p>
                <p>EXP: {randomPoke.base_experience}</p>
            </div>
        );
    }
}

export default Pokecard;
