import React, {Component} from 'react';
import Pokelist from './Pokelist';
import './Pokecard.css';

class Pokecard extends Component {

    render(props){

            return (
            <div className= "Pokecard">
                <h2>{this.props.character.name}</h2>
                <img src = {`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${this.props.character.id}.png`}></img>
                <p>Type: {this.props.character.type}</p>
                <p>EXP: {this.props.character.base_experience}</p>
            </div>
        );
    }
}

export default Pokecard;
